#!/usr/bin/env python3

string = ""

def marker(string, length = 4):
	seen = string[0:length]
	pos = length

	while len(list(set(seen))) != len(string[pos-length:pos]):
		seen = string[pos-length:pos]
		pos +=1

	return pos - 1

print(f"Part 1 : {marker(string)}")
print(f"Part 2 : {marker(string, 14)}")